import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Product } from 'src/app/Model/product';
import { ProductService } from 'src/app/Services/product.service';

@Component({
  selector: 'app-updateproduct',
  templateUrl: './updateproduct.component.html',
  styleUrls: ['./updateproduct.component.css']
})
export class UpdateproductComponent implements OnInit {
  productId!: number;
  product: Product = {
    id: 0,
    name: '',
    price: 0,
    image: ''
  };

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private productService: ProductService
  ) { }

  ngOnInit() {
    this.productId = Number(this.route.snapshot.paramMap.get('id'));
    this.getProduct();
  }

  getProduct() {
    this.productService.getProductById(this.productId).subscribe(
      (product: Product) => {
        this.product = product;
      },
      (error) => {
        console.log(error);
      }
    );
  }

  updateProduct() {
    this.productService.updateProduct(this.product).subscribe(
      () => {
        console.log('Product updated successfully.');
        this.router.navigate(['/admin']); // Redirect to the admin page after successful update
      },
      (error) => {
        console.log(error);
      }
    );
  }

}
<div class="container">
  <div class="blurr">
  <div class="update11">

    <h2>Update Product</h2>
    <form (ngSubmit)="updateProduct()">
      <div class="form-group">
        <label for="name">Product Name</label>
        <input type="text" id="name" name="name" [(ngModel)]="product.name" class="form-control">
      </div>
      <div class="form-group">
        <label for="price">Price</label>
        <input type="number" id="price" name="price" [(ngModel)]="product.price" class="form-control">
      </div>
      <div class="form-group">
        <label for="image">Image URL</label>
        <input type="text" id="image" name="image" [(ngModel)]="product.image" class="form-control">
      </div>
      <button type="submit" class="btn btn-primary">Update</button>
    </form>
  </div>
</div>
  </div>
