import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Product } from 'src/app/Model/product';
import { ProductService } from 'src/app/Services/product.service';

@Component({
  selector: 'app-delete',
  templateUrl: './delete.component.html',
  styleUrls: ['./delete.component.css']
})
export class DeleteComponent {
  productId!: number;
  product: Product = {
    id: 0,
    name: '',
    price: 0,
    image: ''
  };

  successMessage: string = ''; // Add the successMessage property

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private productService: ProductService
  ) { }

  ngOnInit() {
    this.productId = Number(this.route.snapshot.paramMap.get('id'));
    this.getProduct();
  }

  getProduct() {
    this.productService.getProductById(this.productId).subscribe(
      (product: Product) => {
        this.product = product;
      },
      (error) => {
        console.log(error);
      }
    );
  }

  updateProduct() {
    this.productService.updateProduct(this.product).subscribe(
      () => {
        console.log('Product updated successfully.');
        this.router.navigate(['/admin']); // Redirect to the admin page after successful update
      },
      (error) => {
        console.log(error);
      }
    );
  }

  deleteProduct() {
    this.productService.deleteProduct(this.productId).subscribe(
      () => {
        console.log('Product deleted successfully.');
        this.successMessage = 'Successfully deleted';
        this.router.navigate(['/admin']); // Redirect to the admin page after successful deletion
      },
      (error) => {
        console.log(error);
      }
    );
  }
}
