import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Product } from 'src/app/Model/product';
import { ProductService } from 'src/app/Services/product.service';

@Component({
  selector: 'app-update',
  templateUrl: './update.component.html',
  styleUrls: ['./update.component.css']
})
export class UpdateComponent implements OnInit  {
  productId!: number;
  product!: Product;
  error: string='';

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private productService: ProductService
  ) { }

  ngOnInit(): void {
    const idParam = this.route.snapshot.paramMap.get('id');
    if (idParam) {
      this.productId = +idParam;
      this.getProduct(this.productId);
    } else {
      // Handle the case when the ID parameter is missing
      console.error('Product ID parameter is missing.');
    }
  }

  getProduct(id: number): void {
    this.productService.getProductById(id)
      .subscribe(
        (product: Product) => {
          this.product = product;
        },
        (error: any) => {
          this.error = 'Error occurred while retrieving the product.';
          console.error(error);
        }
      );
  }

  updateProduct(): void {
    this.productService.updateProduct(this.product)
      .subscribe(
        (updatedProduct: Product) => {
          console.log('Product updated successfully:', updatedProduct);
          this.router.navigate(['/products']); // Navigate to the products list page
        },
        (error: any) => {
          this.error = 'Error occurred while updating the product.';
          console.error(error);
        }
      );
  }

}


<section class="credit-card">
    <div class="container">
      <div class="card-holder">
        <div class="card-box bg-news">
          <div class="row">
            <div class="col-lg-6">
              <div class="img-box">
                <img src="https://bootdey.com/img/Content/avatar/avatar7.png" class="img-fluid" />
              </div>
            </div>
            <div class="col-lg-6">
              <form (ngSubmit)="updateProduct()">
                <div class="card-details">
                  <h3 class="title">Update Product</h3>
                  <div class="row">
                    <div class="form-group col-sm-7">
                      <div class="inner-addon right-addon">
                        <label for="card-holder">ID</label>
                        <i class="far fa-user"></i>
                        <input id="id" type="text" class="form-control" placeholder="id" aria-label="id" aria-describedby="basic-addon1" [(ngModel)]="product.id" name="id">
                      </div>	
                    </div>
                    <div class="form-group col-sm-5">
                      <label for="">Name</label>
                      <div class="input-group expiration-date">
                        <input type="text" class="form-control" placeholder="name" aria-describedby="basic-addon1" [(ngModel)]="product.name" name="name">

                        <input type="text" class="form-control" placeholder="name" aria-label="name" aria-describedby="basic-addon1" [(ngModel)]="product.name" name="ex">
                      </div>
                    </div>
                    <div class="form-group col-sm-8">
                      <div class="inner-addon right-addon">
                        <label for="card-number">Image URL</label>
                        <i class="far fa-credit-card"></i>
                        <input id="card-number" type="text" class="form-control" placeholder="Card Number" aria-label="Card Holder" aria-describedby="basic-addon1" [(ngModel)]="product.Image" name="cardNumber">
                      </div>	
                    </div>
                    <div class="form-group col-sm-4">
                      <label for="cvc">Price</label>
                      <input id="cvc" type="text" class="form-control" placeholder="CVC" aria-label="Card Holder" aria-describedby="basic-addon1" [(ngModel)]="product.Price" name="cvc">
                    </div>
                    <div class="form-group col-sm-12">
                      <button type="submit" class="btn btn-primary btn-block">Proceed</button>
                    </div>
                  </div>
                </div>
              </form>
            </div><!--/col-lg-6 -->
          </div><!--/row -->
        </div><!--/card-box -->
      </div><!--/card-holder -->
    </div><!--/container -->
  </section>
